import React, { Component } from "react";
import Axios from "axios";

class Main extends Component {
  constructor(props) {
    super(props);
    this.state = { city: "", fromCity: "" };
    //console.log(props);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange = (event) => {
    this.setState({ city: event.target.value });
  };

  handleChangeFromCity = (event) => {
    this.setState({ fromCity: event.target.value });
  };

  handleSubmit = (event) => {
    console.log(this.state.city);
    event.preventDefault();
    Axios.post("http://localhost:1880/test", this.state).then((response) => {
      console.log(response);
    });
  };

  render() {
    return (
      <div>
        <h1>Travel Planner</h1>
        <form onSubmit={this.handleSubmit}>
          <label>
            City:
            <input
              type="text"
              value={this.state.city}
              onChange={this.handleChange}
            />
          </label>
          <label>
            FromCity:
            <input
              type="text"
              value={this.state.fromCity}
              onChange={this.handleChangeFromCity}
            />
          </label>
          <input type="submit" value="Submit" />
        </form>
      </div>
    );
  }
}

export default Main;
